>
  {paymentMethods.map((method, index) => (
    <div
      key={index}
      style={{

        padding: '10px',
        border: '1px solid #ccc',
        borderRadius: '10px',
        boxShadow: '0px 4px 4px 2px rgba(0, 0, 0, 0.25)',
