const express = require('express');
const connectToDatabase = require('./db'); // Database connection module
const generalNewsRoutes = require('./routes/generalNewsRoutes');
const financialNewsRoutes = require('./routes/financialNewsRoutes');
const techNewsRoutes = require('./routes/techNewsRoutes');
const sportsNewsRoutes = require('./routes/sportsNewsRoutes');
const entertainmentNewsRoutes = require('./routes/entertainmentNewsRoutes');
const cors = require('cors');

const app = express();
const port = 4000;

app.use(cors());

// Allow requests from http://localhost:3000
const corsOptions = {
  origin: 'http://localhost:3000',
};

app.use(cors(corsOptions));

// Connect to MongoDB Atlas
(async () => {
  const db = await connectToDatabase();

  app.use(express.json());

  app.use('/api/general-news', generalNewsRoutes);
  // app.use('/api/financial-news', financialNewsRoutes);
  // app.use('/api/tech-news', techNewsRoutes);
  // app.use('/api/sports-news', sportsNewsRoutes);
  // app.use('/api/entertainment-news', entertainmentNewsRoutes);

  app.listen(port, () => {
    console.log(`Server is running on http://localhost:${port}`);
  });
})();
